import React, {Component} from "react";

export class Form extends Component {

    constructor(props) {
        super(props)

        this.state = {
            firstname: '',
            comments: '',
            genre: ''

        }
    }

    handleFirstnameChange = event  => {
        this.setState({
            firstname: event.target.value
        })

    }

    handleCommentsChange = event => {
        this.setState({
            comments: event.target.value
        })

    }

    handleGenreChange = event => {
        this.setState({
            genre: event.target.value
        })
    }

    handleSubmit = event => {
        alert(`${this.state.firstname} ${this.state.comments} ${this.state.genre}`)
        //event.preventDefault()
    }

    render(){
        return (
            <form onSubmit={this.handleSubmit}>
                <div>
                    <label>First Name:</label>
                    <input 
                    type='text' 
                    value={this.state.firstname} 
                    onChange={this.handleFirstnameChange}
                    />

                </div>
                <div>
                    <label> Comments </label>
                    <textarea 
                    value={this.state.comments}
                    onChange={this.handleCommentsChange}></textarea>

                </div>
                <div>
                    <label> Genre </label>
                    <select value={this.state.genre} onChange={this.handleGenreChange}>
                        <option value="scienceFiction">Science Fiction</option>
                        <option value="mystery">Mystery</option>
                        <option value="graphicNovel">Graphic Novel</option>
                        <option value="children">Children</option>
                        <option value="history">History</option>
                    </select>
                </div>
                <button type="submit">SUBMIT</button>
            </form>
        )
    }
}

export default Form
