import React, {Component} from 'react';
import axios from 'axios';

export class Form extends Component {

    constructor(props) {
        super(props)

        this.state = {
            artist: '',
            album: '',
            trackName: '',
            genre: '',
            pic: null,
            comments: ''

        }
    }

    handleArtistChange = event  => {
        this.setState({
            artist: event.target.value
        })

    }

    handleAlbumChange = event  => {
        this.setState({
            album: event.target.value
        })

    }

    handleTrackNameChange = event  => {
        this.setState({
            trackName: event.target.value
        })

    }

    handleGenreChange = event => {
        this.setState({
            genre: event.target.value
        })
    }

    handlePictureChange = event => {
        console.log(event.target.files[0]);

    }

    handlePictureUpload = event => {
        this.setState({
            pic: event.target.files[0]
        })
    }

    handleCommentsChange = event => {
        this.setState({
            comments: event.target.value
        })

    }

    handleSubmit = event => {
        alert(`${this.state.artist} ${this.state.comments} ${this.state.genre}`)
        //event.preventDefault()
    }

    render(){
        return (
            <form onSubmit={this.handleSubmit}>
                <div>
                    <label>Artist:</label>
                    <input 
                    type='text' 
                    value={this.state.artist} 
                    onChange={this.handleArtistChange}
                    />

                </div>

                <div>
                    <label>Album:</label>
                    <input 
                    type='text' 
                    value={this.state.album} 
                    onChange={this.handleAlbumChange}
                    />

                </div>

                <div>
                    <label>Name of Track:</label>
                    <input 
                    type='text' 
                    value={this.state.trackName} 
                    onChange={this.handleTrackNameChange}
                    />

                </div>

                <div>
                    <label> Genre </label>
                    <select value={this.state.genre} onChange={this.handleGenreChange}>
                        <option value="scienceFiction">Science Fiction</option>
                        <option value="mystery">Mystery</option>
                        <option value="graphicNovel">Graphic Novel</option>
                        <option value="children">Children</option>
                        <option value="history">History</option>
                    </select>
                </div>

                <div>
                    <label>Album Image:</label>
                    <input 
                    type='file'
                    onChange={this.handlePictureChange}
                    />
                    <button onClick={this.handlePictureChange}>UPLOAD</button>

                </div>

                <div>
                    <label> Comments </label>
                    <textarea 
                    value={this.state.comments}
                    onChange={this.handleCommentsChange}></textarea>

                </div>

                <button type="submit">SUBMIT</button>
            </form>
        )
    }
}

export default Form
