import React, {Component} from 'react';
import axios from 'axios';
import { CardList } from './card-list/card-list.component';

export class Form extends Component {

    constructor(props) {
        super(props)

        this.state = {
            koreanDrama: [{ 
                id: 'kdw1',
                name: '',
                status: '',
                premiered: '',
                ended: '',
                genres: '',
                pic: null,
                summary: ''
            }]

        }
    }

    componentDidMount(){
        fetch('https://api.tvmaze.com/search/shows?q=anime')
            .then(response => response.json())
            .then(shows => this.setState({koreanDrama: shows}));

            //https://api.tvmaze.com/schedule?country=KR&date=2020-06-20
            //https://api.tvmaze.com/shows
    }


    handleNameChange = event  => {
        this.setState({
            name: event.target.value
        })

    }

    handleStatusChange = event  => {
        this.setState({
            status: event.target.value
        })

    }

    handlePremieredChange = event  => {
        this.setState({
            premiered: event.target.value
        })

    }

    handleEndedChange = event  => {
        this.setState({
            ended: event.target.value
        })

    }

    handleGenresChange = event => {
        this.setState({
            genres: event.target.value
        })
    }

    handlePictureChange = event => {
        console.log(event.target.files[0]);

    }

    /*handlePictureUpload = event => {
        axios.post('');
    }*/

    handleSummaryChange = event => {
        this.setState({
            summary: event.target.value
        })

    }

    handleSubmit = event => {
        alert(`${this.state.name} ${this.state.status} ${this.state.premiered} ${this.state.ended}  ${this.state.genres} ${this.state.pic} ${this.state.summary}`)
        event.preventDefault()


    }

    render(){
        return (
            <form onSubmit={this.handleSubmit}>

                <div>
                    <label>Name Of TV Show:</label>
                    <input 
                    type='text' 
                    value={this.state.name} 
                    onChange={this.handleNameChange}
                    />

                </div>

                <div>
                    <label>Status Of TV Show:</label>
                    <input 
                    type='text' 
                    value={this.state.status} 
                    onChange={this.handleStatusChange}
                    />

                </div>

                <div>
                    <label>Premiered:</label>
                    <input 
                    type='date' 
                    value={this.state.premiered} 
                    onChange={this.handlePremieredChange}
                    />

                </div>

                <div>
                    <label>Ended:</label>
                    <input 
                    type='date' 
                    value={this.state.ended} 
                    onChange={this.handleEndedChange}
                    />

                </div>

                <div>
                    <label> Genres </label>
                    <input 
                    type='text' 
                    value={this.state.genres} 
                    onChange={this.handleGenresChange}
                    />

                </div>

                <div>
                    <label>TV Show Image:</label>
                    <input 
                    type='file'
                    onChange={this.handlePictureChange}
                    />

                </div>

                <div>
                    <label> Summary of TV Show </label>
                    <textarea 
                    value={this.state.summary}
                    onChange={this.handleSummaryChange}></textarea>

                </div>


                <button type="submit">SUBMIT</button>
                <button onClick={this.componentDidMount}>FETCH DATA</button>

                <CardList name="Whitney">
                    <h1>Child</h1>
                </CardList>

                <div className='App'>{
                    this.state.koreanDrama.map(korean => <h1 key={korean.id}>
                        {korean.name}
                    </h1>)
                }

                </div>

            </form>
        )
    }
}

export default Form
